mapper.select:
SELECT DISTINCT scddb.dd_dddh AS ddbh, cp.cp_cpbh AS cpbh, cp.cp_mc AS cpmc, cp.cp_th AS cpth, cp.cp_cz AS cpcz, cp.cp_dhdd AS cptp, cp.kh_bh AS khbh, scddb.ht_bh AS htbh, CONVERT ( VARCHAR ( 100 ), scddb.dd_jhrq, 23 ) AS jhrq, CONVERT ( VARCHAR ( 100 ), scddb.dd_wcrq, 20 ) AS wcrq, scddb.pc_pcsl AS pcsl, scddb.dd_wcsl AS wcsl, cp.cp_dhdd AS dhdd, CONVERT ( VARCHAR ( 100 ), scddb.pc_zdrq, 20 ) AS zdrq, scddb.dd_zt AS ddzt FROM yw_cpxxb cp, ( SELECT DISTINCT ddb.* FROM sc_scddb ddb LEFT JOIN sc_jldjb djb ON ddb.dd_dddh = djb.dd_dddh
AND djb.ry_bh = #{rybh}
) scddb WHERE cp.cp_cpbh = scddb.cp_cpbh AND scddb.dd_zt <> '0'
AND cp.kh_bh = #{khbh}
AND scddb.ht_bh LIKE '%' + #{htbh} + '%'
AND scddb.dd_dddh LIKE '%' + #{ddbh} + '%'
AND cp.cp_mc LIKE '%' + #{cpmc} + '%'
AND DATEDIFF( DAY, #{startDate}, scddb.dd_jhrq ) > 0
AND DATEDIFF( DAY, scddb.dd_jhrq, #{endDate} ) > 0
ORDER BY jhrq

SELECT gxzd.gx_bh AS gxbh, gxzd.gx_mc AS gxmc, cpgx.gx_wcsl AS wcsl, cpgx.gx_fpsl AS fpsl, cpgx.gx_cpsl AS cpsl, cpgx.gx_pcsl AS pcsl FROM sc_cpgxde cpgx, sc_gxzdb gxzd WHERE cpgx.gx_bh = gxzd.gx_bh
AND cpgx.dd_dddh = #{ddbh}
AND cpgx.bm_bh = #{bmbh}

SELECT DISTINCT jl.dd_dddh AS ddbh, jl.cp_cpbh AS cpbh, cp.cp_mc AS cpmc, cp.cp_th AS cpth, cp.cp_cz AS cpcz, CONVERT(varchar(100), dd.dd_jhrq, 23) AS jhrq, dd.pc_pcsl AS pcsl, bm.bm_mc AS bmmc, ry.ry_xm AS ryxm, jl.bm_bh AS bmbh, jl.ry_bh AS rybh FROM sc_jldjb jl, yw_cpxxb cp, sc_scddb dd, rz_bmxxb bm, rz_ryxxb ry WHERE jl.cp_cpbh = cp.cp_cpbh AND jl.dd_dddh = dd.dd_dddh AND jl.bm_bh = bm.bm_bh AND jl.ry_bh = ry.ry_bh
AND jl.dd_dddh = #{ddbh}
AND ry.ry_bh = #{rybh}

SELECT DISTINCT jl.gx_bh as gxbh, gx.gx_mc as gxmc FROM sc_jldjb jl, rz_ryxxb ry, sc_gxzdb gx WHERE jl.ry_bh = ry.ry_bh AND jl.gx_bh = gx.gx_bh
AND jl.dd_dddh = #{ddbh}
AND ry.ry_bh = #{rybh}
order by gxbh

SELECT DISTINCT jl.dj_bh as djbh, isnull( jl.dj_gzdh, '' ) AS gzdh, isnull( jl.dj_ch, '' ) AS jjdh, CONVERT(varchar(100), jl.dj_scrq, 20) as djrq, gx.gx_bh as gxbh, gx.gx_mc as gxmc, gx.gx_lb as gxlb, jl.dj_zpsl as zpsl, jl.dj_fpsl as fpsl, jl.dj_cpsl as cpsl FROM sc_jldjb jl, rz_ryxxb ry , sc_gxzdb gx WHERE jl.ry_bh = ry.ry_bh AND gx.gx_bh = jl.gx_bh
AND jl.dd_dddh = #{ddbh}
AND ry.ry_bh = #{rybh}
AND jl.gx_bh = #{gxbh}
AND jl.dj_gzdh = #{gzdh}
AND jl.dj_ch = #{jjdh}
order by djrq,gxbh

SELECT kh.kh_bh AS khbh, kh.kh_mc AS khmc, kh.kh_gsdz AS gsdz FROM yw_khxxb kh WHERE 1 = 1
AND kh.kh_fl = #{khfl}
AND kh.kh_bh = #{khbh}

SELECT t.gx_bh AS gxbh, t.gx_mc AS gxmc, gx.gx_lb AS gxlb FROM rz_gwgx t, sc_gxzdb gx WHERE t.gx_bh = gx.gx_bh
AND t.ry_bh = #{rybh}
order by gxbh

SELECT t.gx_bh AS gxbh, t.gx_mc AS gxmc, gx.gx_lb AS gxlb FROM rz_gwgx t, sc_gxzdb gx, sc_cpgxde de WHERE t.gx_bh = gx.gx_bh AND t.gx_bh = de.gx_bh
AND t.ry_bh = #{rybh}
AND de.dd_dddh = #{ddbh}
AND t.ry_bh = #{bmbh}
order by gxbh

SELECT DISTINCT djb.dj_gzdh AS gzdh, djb.dd_dddh as ddbh, djb.cp_cpbh as cpbh FROM sc_jldjb djb WHERE 1 = 1 AND djb.dj_gzdh IS NOT NULL
AND djb.dd_dddh = #{ddbh}
AND djb.cp_cpbh= #{cpbh}
AND djb.dj_gzdh IS NOT NULL order by gzdh

SELECT DISTINCT djb.dj_ch AS jjdh, djb.dd_dddh as ddbh, djb.cp_cpbh as cpbh FROM sc_jldjb djb WHERE 1 = 1 AND djb.dj_ch IS NOT NULL
AND djb.dd_dddh = #{ddbh}
AND djb.cp_cpbh= #{cpbh}
AND djb.dj_ch IS NOT NULL order by jjdh

SELECT SUM ( djb.dj_wcsl ) AS wcsl, djb.dd_dddh AS ddbh, djb.cp_cpbh AS cpbh, djb.gx_bh AS gxbh, djb.dj_gzdh AS gzdh FROM sc_jldjb djb WHERE 1 = 1
AND djb.dd_dddh = #{ddbh}
AND djb.cp_cpbh = #{cpbh}
AND djb.gx_bh = #{gxbh}
AND djb.dj_gzdh = #{gzdh}
GROUP BY djb.dd_dddh, djb.cp_cpbh, djb.gx_bh, djb.dj_gzdh

SELECT
de.yl1 AS jjgx,
de.gx_pcsl AS pcsl
FROM
sc_cpgxde de
WHERE 1 = 1

    AND de.dd_dddh = #{ddbh}


    AND de.cp_cpbh = #{cpbh}



    AND de.gx_bh = #{gxbh}

SELECT dd.dd_dddh AS ddbh, cp.cp_cpbh AS cpbh, cp.cp_mc AS cpmc, cp.cp_th AS cpth, cp.cp_cz AS cpcz, CONVERT ( VARCHAR ( 100 ), dd.dd_jhrq, 23 ) AS jhrq, dd.pc_pcsl AS pcsl, ry.ry_bh AS rybh, ry.ry_xm AS ryxm, bm.bm_bh AS bmbh, bm.bm_mc AS bmmc FROM yw_cpxxb cp, sc_scddb dd, rz_ryxxb ry, rz_bmxxb bm WHERE 1 = 1 AND cp.cp_cpbh = dd.cp_cpbh AND ry.bm_bh = bm.bm_bh
AND dd.dd_dddh = #{ddbh}
AND ry.ry_bh = #{rybh}

SELECT SUM( scjl.mx_sl ) sl
FROM
sc_scjlb scjl
WHERE
1 = 1

    AND scjl.dd_dddh = #{ddbh}


    AND scjl.gx_bh = #{gxbh}


    AND scjl.mx_fclb  = #{fclb}

SELECT SUM( jlb.mx_sl ) sl,
jlb.dd_dddh as ddbh,
jlb.gx_bh as gxbh
FROM
sc_scjlb jlb
WHERE 1 = 1

    AND jlb.dd_dddh = #{ddbh}

AND jlb.gx_bh IN ( SELECT DISTINCT gxde.yl1 AS gxbh FROM sc_cpgxde gxde WHERE  1 = 1

    AND gxde.dd_dddh = #{ddbh}


    AND gxde.gx_bh = #{gxbh}

)
group by jlb.dd_dddh, jlb.gx_bh

SELECT jlb.mx_yy AS yybh, jlb.gx_bh AS gxbh, dzb.gx_mc AS gxmc, SUM ( jlb.mx_sl ) AS sl, dzb.yy AS yy FROM sc_scjlb jlb, zg_fcdzb dzb WHERE jlb.gx_bh = dzb.gx_bh AND jlb.mx_yy = dzb.yy_bh
AND jlb.dd_dddh = #{ddbh}
AND jlb.ry_bh = #{rybh}
AND jlb.gx_bh = #{gxbh}
AND jlb.mx_fclb = #{fclb}
AND dzb.tjbz = '0'
AND dzb.tjbz <> '0'
GROUP BY jlb.mx_yy, jlb.gx_bh, dzb.yy, dzb.gx_mc
ORDER BY jlb.mx_yy

SELECT SUM( scjl.mx_sl ) sl, scjl.dd_dddh AS ddbh, scjl.gx_bh AS gxbh, scjl.mx_fclb AS fclb, scjl.mx_yy AS yybh, scjl.ry_bh AS rybh FROM sc_scjlb scjl WHERE 1 = 1
AND scjl.dd_dddh = #{ddbh}
AND scjl.ry_bh = #{rybh}
AND scjl.mx_yy = #{yybh}
AND scjl.gx_bh = #{gxbh}
AND scjl.mx_fclb = #{fclb}
GROUP BY scjl.dd_dddh, scjl.gx_bh, scjl.mx_fclb, scjl.mx_yy, scjl.ry_bh
